//import { render } from '@testing-library/react';
//import 'semantic-ui-css';
//import { render } from '@testing-library/react';
import { render } from 'react-dom';
import React, { Component } from 'react';
import { Button, Form, Container, Header, Dropdown } from 'semantic-ui-react';
import './interestForm.css';


// const InterestForm = () => {
// export default function InterestForm () {
class InterestForm extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            name: '',
            email: '',
            currResidence: '',
            currInstitution: '',
            residence: '',
            interest: '',
            USCdepartment: '',
            message: ''
        }
    }

        changeHandler = (e) => {
            this.setState({[e.target.name] : e.target.value})
        }

        submitHandler = e => {
        e.preventDefault();
        console.log(this.state);
    }

    render() {
        const { name, email, currResidence, currInstitution, position, interest, USCdepartment, message} = this.state;
        return (
            <Container fluid className="form-container">
            <Header as='h2'>Interest Form</Header>
            <Form className="interest-form" onSubmit={this.submitHandler}>
                <Form.Field className="form-field">
                    <label>Name</label>
                    <input placeholder='Name' type="text" name="name" value={name} onChange={this.changeHandler} />
                </Form.Field>
                <Form.Field className="form-field">
                    <label>E-Mail</label>
                    <input placeholder='E-mail' type="text" name="name" value={name} onChange={this.changeHandler} />
                </Form.Field>
                <Form.Field className="form-field">
                    <label>Current place/country of residence</label>
                    <input placeholder='Residence' type="text" name="name" value={name} onChange={this.changeHandler} />
                </Form.Field>
                <Form.Field className="form-field">
                    <label>Current or most recent affiliation (institution)</label>
                    <input placeholder='Institution' type="text" name="name" value={name} onChange={this.changeHandler} />
                </Form.Field>

                {/* dropdown here */}
                <Form.Field className="form-field">

                    <label>Current Position</label>
                    <select value={position} onChange={this.changeHandler}>
                        <option value="lime">Fsflejfls</option>
                    </select>


                </Form.Field>

                <Form.Field className="form-field">
                    <label>Your primary area(s) of interest</label>
                    <input placeholder='Interests' type="text" name="name" value={name} onChange={this.changeHandler} />
                </Form.Field>
                <Form.Field className="form-field">
                    <label>Your primary choise of School/Department/Program at USC</label>
                    <input placeholder='School/Department' type="text" name="name" value={name} onChange={this.changeHandler} />
                </Form.Field>
                <Form.Field className="form-field">
                    <label>Your Message</label>
                    <textarea rows="3" className="message-field" placeholder='Please tell us a little about yourself and what kinds of opportunities you are looking for.' type="textarea" name="message" value={message} onChange={this.changeHandler} />
                </Form.Field>

                <Button className="submit-button" type='submit'>Submit</Button>
            </Form>
            </Container>
        )
    }
}

render(<InterestForm />, document.getElementById("root"));
export default InterestForm;
